import React, { useCallback, useEffect, useRef, useState } from 'react';


const ViewCase = () => {
   
    const checkActiveIndex = useCallback(() => {
        const paths = location.pathname.split('/');
        const currentPath = paths[paths.length - 1];

        switch (currentPath) {
            case 'contact':
                setActiveIndex(1);
                break;
            case 'productlines':
                setActiveIndex(2);
                break;
            case 'communications':
                setActiveIndex(3);
                break;
            default:
                break;
        }
    }, [location]);
    useEffect(() => {
        checkActiveIndex();
    }, [checkActiveIndex]);

   return(
    <div className="grid" style={{ justifyContent: 'space-between' }}>
                <div className="col-12 xl:col-12">

                    <div className="card card-w-title">

                        <TabMenu model={wizardItems} activeIndex={activeIndex} onTabChange={(e) => setActiveIndex(e.index)} />
                        <Routes>
                            <Route exact path={'/'} element={<CompanyInfo />} />
                            <Route path={'/contact'} element={<Contact />} />
                            <Route path={'/productlines'} element={<ProductLines />} />
                            <Route path={'/communications'} element={<Communications />} />
                        </Routes>
                    </div>

                </div>
            </div>
   );
};
export default ViewCase;
