import React, { useState, useEffect, useRef } from 'react';
import { Button } from 'primereact/button';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';
import data from '../assets/flags/products.json';

const Products = () => {
    
    return (
        
        <div className="layout-dashboard" >
            <div className="col-12 lg:col-6 xl:col-2" style={{marginLeft:'90%'}}>
                        <Button label="Back" icon="pi pi-chevron-left" style={{backgroundColor:'red', borderBlockColor:'red'}} className="mr-2 mb-2" ></Button>
                  </div>
          <div className= "grid" style={{backgroundColor:'white', marginBottom:'2%'}} >
                  <div className="col-12 lg:col-6 xl:col-6"  >
                      <div style={{width: '250%'}}>
                      <InputText id="firstname1" type="text" placeholder="Enter any information about a product" style={{width: '40%'}}/>
                      </div>
                  </div>
                  <div className="col-12 lg:col-6 xl:col-2" >
                        <Button label="Search" icon="pi pi-search" className="mr-2 mb-2" ></Button>
                  </div>
          </div>
            

          <div className="grid" style={{justifyContent: 'space-evenly'}}>
                <div className="col-12 xl:col-12">
                    <div className="card card-w-title global-sales" >
                        <DataTable value={data} paginator rows={5} className="p-datatable-products">
                        <Column field="id" header="Product ID" ></Column>
                            <Column field="productName" header="Product Name" ></Column>
                            <Column field="location" header="Description" ></Column>
                            <Column field="contact" header="Price" ></Column>
                            <Column field="name" header="Unit of Measure" ></Column>
                            <Column field="name" header="Status" ></Column>
                            <Column field="name" header="Options" ></Column>
                            <Column field="options" header="Options" body={<><Link to={"/Clients"} style={{ color: 'green' }}>View</Link><p></p>      <Link to={"/Clients"} style={{ color: 'red' }}>Manage</Link></>} >

                            </Column>
                        </DataTable>
                    </div>
                </div>               
            </div>
            </div>
    );
};
export default Products;
