import { Route, Routes, useNavigate, useLocation } from 'react-router-dom';
import React, { useCallback, useEffect, useRef, useState } from 'react';
import { Steps } from 'primereact/steps';
import { Card } from 'primereact/card';

const PaymentDemo = () => {
    const [activeIndex, setActiveIndex] = useState(0);

    const navigate = useNavigate();
    const location = useLocation();

    const checkActiveIndex = useCallback(() => {
        const paths = location.pathname.split('/');
        const currentPath = paths[paths.length - 1];

        switch (currentPath) {
            case 'seat':
                setActiveIndex(1);
                break;
            case 'payment':
                setActiveIndex(2);
                break;
            case 'confirmation':
                setActiveIndex(3);
                break;
            default:
                break;
        }
    }, [location]);

    useEffect(() => {
        checkActiveIndex();
    }, [checkActiveIndex]);

    const wizardItems = [
        { label: 'Lead', command: () => navigate('/menu') },
        { label: 'Prospect', command: () => navigate('/menu') },
        { label: 'Full Client', command: () => navigate('/menu') }

    ];
    return (
        <div className="col-12 md:col-12">
            <div className="card card-w-title">

                <Steps model={wizardItems} activeIndex={activeIndex} onSelect={(e) => setActiveIndex(e.index)} readOnly={false} />
                {/* <Routes>
                        <Route exact path={'/'} element={<PersonalDemo />} />
                    </Routes> */}
                <div className="card">
                    <Card title="Case No: TP-6839">
                        <p className="m-0">
                            Date Reported: 16/08/2023
                        </p>
                        <p>By: Shamira Naiga</p>
                        <h3 style={{ textAlign: 'center', fontWeight: 'bolder' }}>NEW</h3>
                    </Card>
                    <p>Case Details</p>
                    <p>Attachments</p>
                    <p>Case Notes</p>
                    <p>Logs</p>
                </div>
            </div>
        </div>
    );
};

export default PaymentDemo;
