import React, { useCallback, useEffect, useRef, useState } from 'react';
import { Menubar } from 'primereact/menubar';
import { InputText } from 'primereact/inputtext';
import { BreadCrumb } from 'primereact/breadcrumb';
import { Steps } from 'primereact/steps';
import { TabMenu } from 'primereact/tabmenu';
import { TieredMenu } from 'primereact/tieredmenu';
import { Menu } from 'primereact/menu';
import { Button } from 'primereact/button';
import { ContextMenu } from 'primereact/contextmenu';
import { MegaMenu } from 'primereact/megamenu';
import { PanelMenu } from 'primereact/panelmenu';
import { Route, Routes, useNavigate, useLocation } from 'react-router-dom';
import PersonalDemo from './menu/PersonalDemo';
import ConfirmationDemo from './menu/ConfirmationDemo';
import PaymentDemo from './menu/PaymentDemo';
import SeatDemo from './menu/SeatDemo';

const MenuDemo = () => {
    const [activeIndex, setActiveIndex] = useState(0);

    const navigate = useNavigate();
    const location = useLocation();

    const checkActiveIndex = useCallback(() => {
        const paths = location.pathname.split('/');
        const currentPath = paths[paths.length - 1];

        switch (currentPath) {
            case 'seat':
                setActiveIndex(1);
                break;
            case 'payment':
                setActiveIndex(2);
                break;
            case 'confirmation':
                setActiveIndex(3);
                break;
            default:
                break;
        }
    }, [location]);

    useEffect(() => {
        checkActiveIndex();
    }, [checkActiveIndex]);

   
            
               

    const wizardItems = [
        { label: 'Personal', command: () => navigate('/menu') },
        { label: 'Seat', command: () => navigate('/menu/seat') },
        { label: 'Payment', command: () => navigate('/menu/payment') },
        { label: 'Confirmation', command: () => navigate('/menu/confirmation') }
    ];

   

                 
                
                   
       
          
        

   

    return (
        <div className="grid p-fluid">

            <div className="col-12 md:col-12">
                <div className="card card-w-title">
                    <h5>TabMenu</h5>
                    <TabMenu model={wizardItems} activeIndex={activeIndex} onTabChange={(e) => setActiveIndex(e.index)} />
                    <Routes>
                        <Route exact path={'/'} element={<PersonalDemo />} />
                        <Route path={'/confirmation'} element={<ConfirmationDemo />} />
                        <Route path={'/payment'} element={<PaymentDemo />} />
                        <Route path={'/seat'} element={<SeatDemo />} />
                    </Routes>
                </div>
            </div>
        </div>
    );
};

export default MenuDemo;
